document.addEventListener("DOMContentLoaded",()=>{const tasksList=document.querySelector(".tasks");tasksList.addEventListener("click",event=>{const deleteButton=event.target.closest(".delete-btn");if(deleteButton){const taskContainer=deleteButton.closest(".task-container");taskContainer&&(taskContainer.classList.add("fade-out"),taskContainer.remove())}})}),document.addEventListener("DOMContentLoaded",()=>{const newTaskButton=document.querySelector(".new-task-button"),newTaskPopup=document.querySelector(".new-task-popup"),submitButton=document.getElementById("submitBtn"),tasksList=document.querySelector(".tasks"),overlay=document.querySelector(".overlay"),newTaskInput=document.getElementById("newTask");let taskIdCounter=1;function addTask(){const newTaskInputValue=newTaskInput.value.trim();if(""!==newTaskInputValue){const taskId=`customCheckbox${taskIdCounter}`,taskContainer=document.createElement("div");taskContainer.classList.add("task-container"),taskContainer.innerHTML=`\n                <input type="checkbox" name="To-do" id="${taskId}" class="customCheckbox">\n                <label for="${taskId}" class="task-box">${newTaskInputValue}</label>\n                <div class="task-delete delete-btn">\n                    <i class="fa-solid fa-trash"></i>\n                </div>\n            `,tasksList.appendChild(taskContainer),taskIdCounter++,newTaskInput.value="",newTaskPopup.style.display="none",overlay.style.display="none"}else alert("Please enter a task!")}newTaskButton.addEventListener("click",event=>{event.preventDefault(),newTaskPopup.style.display="block",overlay.style.display="block",newTaskInput.focus()}),document.addEventListener("click",event=>{event.target.closest(".new-task-popup")||event.target===newTaskButton||(newTaskPopup.style.display="none",overlay.style.display="none")}),submitButton.addEventListener("click",addTask),newTaskInput.addEventListener("keydown",event=>{"Enter"===event.key&&addTask()})});